import React, { Component } from 'react'

class Todo extends Component {
  render() {
    return (
      <div className='card-body'>
        <li className='text-center'>
          {this.props.task}
          <button name='delete' className='btn btn-outline-light' type='button' onClick={() => this.props.delete(this.props.id)}>X</button>
        </li>
      </div>
    )
  }
}

export default Todo
