import React, { Component } from 'react'

class Todo extends Component {
  render() {
    let style = {border: '1px solid #F1F1F1', padding: '20px 40px'}
    return (
      <li style={style} className='card-body text-center'>
        {this.props.task}
        <button nme='delete' type='button' onClick={() => this.props.delete(this.props.id)}>X</button>
      </li>
    )
  }
}

export default Todo
